import { DistinctDays } from "../functions/DistinctDays"
import { FormatDate } from "../functions/FormatDate.js";
import { useSelector, useDispatch } from "react-redux";
import "../styles/dayselector.scss"


const DaySelector = () => {

    const currentWeatherData = state => state.forecast;
    const  cityCurrentWeatherData = useSelector(currentWeatherData);
    const currentDay = state => state.currentDay;
    const  cityCurrentDay = useSelector(currentDay);

    const dispatch = useDispatch();

    if (cityCurrentWeatherData != null) {

        const distinctDays = DistinctDays(cityCurrentWeatherData)

        if (distinctDays.find(day => day == cityCurrentDay) == undefined) {
            dispatch({ type: 'api/putCurrentDayInState', payload: distinctDays[0]})
        }
        
        const listOfDays = distinctDays.map( day => {
            if (day == cityCurrentDay) {
                return <div className = "day-selector__day day-selector__day--current">{FormatDate(day)}</div>
            }
            else {
                return <button className = "day-selector__day" onclick = { () => {
                    console.log("dispatching new current day")
                    dispatch({ type: 'api/putCurrentDayInState', payload: day})
                }
            }>{FormatDate(day) }</button>
            }
        })
        return <div className = "day-selector">{listOfDays}</div>
    }
    else {
        return <div></div>
    }
}
export default DaySelector
