import { GetMatchingCities } from "../functions/GetMatchingCities"
import { useEffect, useState} from "react";
import { useDispatch } from "react-redux";

const CityAutoSuggest = (props) => {
    
        const [suggestions, setSuggestions] = useState(null);
        const dispatch = useDispatch();
    
        useEffect(() => {
        if (props.city.length > 2) {
        /*fetch("//geodb-free-service.wirefreethought.com/v1/geo/cities/?namePrefix="+props.city+"&sort=-population&types=CITY&limit=10")*/
        fetch("https://api.mapbox.com/geocoding/v5/mapbox.places/"+props.city+".json?access_token=pk.eyJ1IjoiY2xlbWVudGJlbmV6ZWNoIiwiYSI6ImNrdmRweXpxMjM3cjUybmx1dG9za24yOGoifQ.8SmT7lwTGEMypFRgCD-HLw+types=place") 
        
        .then(response => response.json())
        .then(data => {
            let citiesReactElements = [];
                citiesReactElements =  data.features.map(element => {
                    if (element.text.includes("arrondissement")) {
                        /*ignore this element type as it will not work for openweather api*/
                    } else {
                    return(
                        <div className = "city-search-form__auto-suggest__city" onClick = {(e) => {
                            e.preventDefault();
                            dispatch({ type: 'api/putCityInState', payload: element.text+", "+element.context[2].short_code})
                            setSuggestions(null);
                        }}>{element.place_name}</div>
                        )
                    }
                })
            setSuggestions(citiesReactElements);
            console.log(suggestions);
                
        });
    } else {
        setSuggestions(null);
    }
    }, [props.city]);
        if (suggestions !== null) {
            return (
                <div className = "city-search-form__auto-suggest">
                    <div>{suggestions}</div>
                </div>)
        } else {
            return (
                <div className = "city-search-form__auto-suggest">
                    <div></div>
                </div>)
        }
       

      
}
export default CityAutoSuggest
