import React, {useState} from "react";
import { OWCityGetData } from "../functions/OWCityGetData";

const CitySearchForm = () => {

    /*Setting up hooks for fields
    These will be used to maintain a state for the form and allow the send function to work its magic*/
    const [city, setCity] = React.useState("");

    const getForecast = () => {
        OWCityGetData("rennes", "forecast");
    }

    const getCurrentWeather = () => {
        OWCityGetData("rennes", "weather");
    }

    return (
    <form onSubmit = {() => {
        OWCityGetData("rennes", "forecast")
    }}>
        <input name = "city" value = {city} type = "text" onChange={e => {
                    setCity(e.target.value);}}>
        </input>
        <button type = "submit">{"Voir les prévisions"}</button>
    </form>
    )

}
export default CitySearchForm
