import { weatherIcons } from "./constants"
import { temperatureIcon, windIcon,windDirectionIcon, cloudCoverage, rainAmount } from "./constants"
import { useDispatch, useSelector } from "react-redux"
import { GetWindDirection } from "../functions/GetWindDirection"

const ThreeHoursForecast = (props) => {
    
    const currentStoreTime = state => state.currentTime;
    const storeTime = useSelector(currentStoreTime)

    const currentStoreTimezone = state => state.timezone;
    const storeTimezone = useSelector(currentStoreTimezone)

    const localTime = props.forecast.dt + storeTimezone + new Date(props.forecast.dt * 1000).getTimezoneOffset()*60;
    
    const currentTime = new Date(localTime* 1000).toLocaleTimeString("fr-FR")
    const currentTimeHHMM = currentTime.substring(0, 5)
    
    const weatherIcon = weatherIcons.filter(element => element.label === props.forecast.weather[0].main)[0].icon
    const weatherIconColor = weatherIcons.filter(element => element.label === props.forecast.weather[0].main)[0].label

    const dispatch = useDispatch();



    let cardClass = "forecast__card";
    let timeWeatherClass = "forecast__card__time-weather";
    let rainInPeriod = "-"
    let rainAmountDisplayValue = null;

    const displayRainValue = () => {
        return(
        <div className = "forecast__card__parameter">
            <i className={rainAmount +" forecast__card__parameter__icon forecast__card__parameter__icon--rain-amount"}></i>
            <div className = "forecast__card__parameter__value">{rainInPeriod+" mm"}</div>
        </div>
        )
    }

    if (props.forecast.dt == storeTime) {
        cardClass = cardClass+" "+cardClass+"--selected"
        timeWeatherClass = timeWeatherClass+" "+timeWeatherClass+"--selected"
    }

    if (props.forecast.rain !== undefined) {
        rainInPeriod = props.forecast.rain['3h'];
        rainAmountDisplayValue = displayRainValue();
    }
    
    return (
    <div className = {cardClass} onClick = {() => {
        console.log(props.forecast.dt)
        dispatch({ type: 'api/putCurrentTimeInState', payload: props.forecast.dt})
    }
    }>
        <div className = {timeWeatherClass}>
            <div className = "forecast__card__time-weather__time">{currentTimeHHMM}</div>
            <i className={weatherIcon+" forecast__card__time-weather__icon forecast__card__time-weather__icon--"+weatherIconColor}></i>
        </div>
        {/*<div>{props.forecast.weather[0].description}</div>*/}
        <div className = "forecast__card__data-container">
            <div className = "forecast__card__parameter forecast__card__parameter--temp">
                <i className={temperatureIcon +" forecast__card__parameter__icon forecast__card__parameter__icon--temp"}></i>
                <div className = "forecast__card__parameter__value">{Math.round(props.forecast.main.temp)+" 째C"}</div>
            </div>
            <div className = "forecast__card__parameter forecast__card__parameter--wind">
                <i className={windIcon +" forecast__card__parameter__icon forecast__card__parameter__icon--wind"}></i>
                <div className = "forecast__card__parameter__value">{Math.round(props.forecast.wind.speed)+" - "+Math.round(props.forecast.wind.gust) +" m/s"}</div>
            </div>
            <div className = "forecast__card__parameter forecast__card__parameter--wind-direction">
                <i className={windDirectionIcon +" forecast__card__parameter__icon forecast__card__parameter__icon--wind-direction"}></i>
                <div className = "forecast__card__parameter__value">{GetWindDirection(props.forecast.wind.deg)+" ("+props.forecast.wind.deg+"째)"}</div>
            </div>
            <div className = "forecast__card__parameter">
                <i className={cloudCoverage +" forecast__card__parameter__icon forecast__card__parameter__icon--cloud-coverage"}></i>
                <div className = "forecast__card__parameter__value">{props.forecast.clouds.all+" %"}</div>
            </div>
            {rainAmountDisplayValue}
        </div>
        
    </div>
    )
}

export default ThreeHoursForecast
