import { weatherIcons } from "./constants"

const ThreeHoursForecast = (props) => {
    const currentDate = new Date(props.forecast.dt * 1000).toLocaleDateString("fr-FR")
    const currentTime = new Date(props.forecast.dt * 1000).toLocaleTimeString("fr-FR")
    const weatherIcon = weatherIcons.filter(element => element.label === props.forecast.weather[0].main)
    console.log(weatherIcon)
    console.log(props.forecast.weather[0].main)

    return (
    <div className = "forecast__card">
        <div>{currentTime}</div>
        <i className={weatherIcon}></i>
        <div>{props.forecast.weather[0].description}</div>
        <div>{props.forecast.main.temp}</div>
        <div>{props.forecast.wind.speed}</div>
        <div>{props.forecast.wind.gust}</div>
        <div>{props.forecast.wind.deg}</div>
        
    </div>
    )
}

export default ThreeHoursForecast
