import { weatherIcons } from "./constants"
import { temperatureIcon, windIcon,windDirectionIcon } from "./constants"

const ThreeHoursForecast = (props) => {
    const currentTime = new Date(props.forecast.dt * 1000).toLocaleTimeString("fr-FR")
    const currentTimeHHMM = currentTime.substring(0, 4)
    const weatherIcon = weatherIcons.filter(element => element.label === props.forecast.weather[0].main)[0].icon


    return (
    <div className = "forecast__card">
        <div>{currentTimeHHMM}</div>
        <i className={weatherIcon}></i>
        <div>{props.forecast.weather[0].description}</div>
        <div>
        <i class={temperatureIcon}></i>
            {Math.round(props.forecast.main.temp)}
        </div>
        <div>
            <i class={windIcon}></i>
                {Math.round(props.forecast.wind.speed)+" - "+Math.round(props.forecast.wind.gust)}
        </div>
        <div>
        <i class={windDirectionIcon}></i>
            {props.forecast.wind.deg}
        </div>
        
    </div>
    )
}

export default ThreeHoursForecast
