import "../styles/DetailedForecast.scss"
import { useSelector } from "react-redux"
import { weatherIcons } from "./constants"
import { FormatDate } from "../functions/FormatDate"
import Fade from "react-reveal"

const DetailedForecast = (props) => {


    let weatherIcon;
    let weatherIconColor;

    const currentTime = state => state.currentTime;
    const cityCurrentTime = useSelector(currentTime);

    /*const currentForecast = state => state.forecast;
    const cityCurrentForecast = useSelector(currentForecast);*/


    if (props.forecast.data.find( element => element.dt === cityCurrentTime) !== undefined) {

    const forecastData = props.forecast.data.find( element => element.dt === cityCurrentTime);

    const currentIcons = forecastData.weather.map( weather => {
        weatherIcon = weatherIcons.filter(element => element.label === weather.main)[0].icon
        weatherIconColor = weatherIcons.filter(element => element.label === weather.main)[0].label
        return <i className={weatherIcon+" forecast__card__time-weather__icon forecast__card__time-weather__icon--"+weatherIconColor}></i>
    })

    const current = new Date(forecastData.dt * 1000).toLocaleTimeString("fr-FR")
    console.log(current)
    const currentTimeHHMM = current.substring(0, 5)

    

    return (
        <Fade bottom><div className = "detailed-forecast">
                    <div className = "detailed-forecast__data-container">
                            {currentIcons}
                            <div className = "detailed-forecast__time">{currentTimeHHMM}</div>
                        </div>
                    <div className = "detailed-forecast__data-container">
                        <div className = "detailed-forecast__data-label">{"Temperature"}</div>
                        <div className = "detailed-forecast__data">{forecastData.main.temp+" °C"}</div>
                    </div>
                    <div className = "detailed-forecast__data-container">
                        <div className = "detailed-forecast__data-label">{"Ressenti"}</div>
                        <div className = "detailed-forecast__data">{forecastData.main.feels_like+" °C"}</div>
                    </div>
                    <div className = "detailed-forecast__data-container">
                        <div className = "detailed-forecast__data-label">{"Pression Atmosphérique"}</div>
                        <div className = "detailed-forecast__data">{forecastData.main.grnd_level+" hPa"}</div>
                    </div>
                    <div className = "detailed-forecast__data-container">
                        <div className = "detailed-forecast__data-label">{"Humidité"+" %"}</div>
                        <div className = "detailed-forecast__data">{forecastData.main.humidity}</div>
                    </div>
                    <div className = "detailed-forecast__data-container">
                        <div className = "detailed-forecast__data-label">{"Couverture nuageuse"}</div>
                        <div className = "detailed-forecast__data">{forecastData.clouds.all}</div>
                    </div>
                    <div className = "detailed-forecast__data-container">
                        <div className = "detailed-forecast__data-label">{"Vent Moyen"}</div>
                        <div className = "detailed-forecast__data">{forecastData.wind.speed}</div>
                    </div>
                    <div className = "detailed-forecast__data-container">
                        <div className = "detailed-forecast__data-label">{"Rafales"}</div>
                        <div className = "detailed-forecast__data">{forecastData.wind.speed}</div>
                    </div>

        </div></Fade>

    )
    } else {
        return null;
    }
    }
export default DetailedForecast
