export const DistinctDays = (cityForecastWeatherData) => {
 
    //Finding out all distinct dates in the five days forecast
    const distinctDays = cityForecastWeatherData.list.map(forecastData => new Date((forecastData.dt + cityForecastWeatherData.city.timezone + new Date(forecastData.dt*1000).getTimezoneOffset()*60)*1000).toLocaleDateString("en-US"))
    .filter((value, index, self) => self.indexOf(value) === index)
    return distinctDays
   }
