import { useSelector } from "react-redux";
import store from "./Store.js";
import "../styles/forecast.scss"
import { temperatureIcon, windIcon, windDirectionIcon } from "./constants.js";
import { weatherIcons } from "./constants.js";
import { GetWindDirection } from "../functions/GetWindDirection"

const CityCurrentWeather = () => { 

    const currentWeatherData = state => state.currentWeather;
    const  cityCurrentWeatherData = useSelector(currentWeatherData);
    

    if (cityCurrentWeatherData != null) {        
        
    if (cityCurrentWeatherData !== "error") {
        const weatherIcon = weatherIcons.filter(element => element.label === cityCurrentWeatherData.weather[0].main)[0].icon
        const weatherIconColor = weatherIcons.filter(element => element.label === cityCurrentWeatherData.weather[0].main)[0].label

        let gust;
        
        if (cityCurrentWeatherData.wind.gust) {
            gust = " - "+Math.round(cityCurrentWeatherData.wind.gust)
        } else {
            gust = "";
        }

    return (
            <div className = "forecast__card forecast__card--current">
                <div className = "forecast__card__time-weather">
                    <div className = "forecast__card__time-weather__time">{"Actuellement..."}</div>
                    <i className={weatherIcon+" forecast__card__time-weather__icon forecast__card__time-weather__icon--"+weatherIconColor+" forecast__card__time-weather__icon--big"}></i>
                </div>
                {/*<div>{props.forecast.weather[0].description}</div>*/}
                <div className = "forecast__card__city">{cityCurrentWeatherData.name+", "+cityCurrentWeatherData.sys.country}</div>
                    {/*<div>{cityCurrentWeatherData.weather[0].main}</div>*/}
                
                <div className = "forecast__card__parameters-container">
                    <div className = "forecast__card__temp">
                        <i className={temperatureIcon +" forecast__card__temp__icon"}></i>
                        <div className = "forecast__card__temp__value">{Math.round(cityCurrentWeatherData.main.temp)}</div>
                    </div>
                    <div className = "forecast__card__wind">
                        <i className={windIcon +" forecast__card__wind__icon"}></i>
                        <div className = "forecast__card__wind__value">{Math.round(cityCurrentWeatherData.wind.speed)+gust}</div>
                    </div>
                    <div className = "forecast__card__wind-direction">
                        <i className={windDirectionIcon +" forecast__card__wind-direction__icon"}></i>
                        <div className = "forecast__card__wind-direction__value">{GetWindDirection(cityCurrentWeatherData.wind.deg)+" ("+cityCurrentWeatherData.wind.deg+"°)"}</div>
                    </div>
                </div>
            </div>)
        } 
        else {
            return (
                <div className = "forecast__card forecast__card--current">
                    <div className = "forecast__card__time-weather__time">{"So sorry, I could not find this city :("}</div>
                </div>
            )
        }}
        else {
            return null;
        }

    
}
export default CityCurrentWeather
