import { Line, LineChart, XAxis, Tooltip, YAxis, CartesianGrid } from 'recharts';
import "../styles/Charts.scss"
import { useSelector } from 'react-redux';
import { GroupForcastByDay } from "../functions/GroupForcastByDay.js";
import { ResponsiveContainer } from 'recharts';


const TempChart= (props) => {

    const currentStoreDay = state => state.currentDay
    const currentDay = useSelector(currentStoreDay)

    const forecastWeatherData = state => state.forecast;
    const cityForecastWeatherData = useSelector(forecastWeatherData);

    const currentStoreTime = state => state.currentTime;
    const storeTime = useSelector(currentStoreTime)

    const currentStoreTimezone = state => state.timezone;
    const storeTimezone = useSelector(currentStoreTimezone)

    let tempChartData = [];

    if (currentDay !== null && cityForecastWeatherData !== null) {
    
        const forecastsGroupedByDay = GroupForcastByDay(cityForecastWeatherData);
        const dayForecast = forecastsGroupedByDay.find(element => element.date === currentDay)
        dayForecast.data.map(element => {
            
            let localTime = element.dt + storeTimezone + new Date(element.dt * 1000).getTimezoneOffset()*60;        
            let currentTime = new Date(localTime* 1000).toLocaleTimeString("fr-FR")
            let currentTimeHHMM = currentTime.substring(0, 5)
            tempChartData.push({'time': currentTimeHHMM, 'temperature' : Math.round(element.main.temp)})
        });




    return (
    <ResponsiveContainer width="100%" height={280}>    
    <LineChart
          data={tempChartData}
          margin={{ top: 5, right: 20, left: 10, bottom: 5 }}
      >
          <XAxis dataKey="time"/>
          <YAxis dataKey="temperature"/>


          <Tooltip />

          {/*<Line type="monotone" dataKey="time" stroke="red" yAxisId={0} label={{ fill: 'red', fontSize: 20, fontWeight: "bold", background: "white" }} />*/}
          <Line type="monotone" dataKey="temperature" stroke="#00bdef" strokeWidth={15} label={{ fill: 'black', fontSize: 25 }} dot={{ fill: "transparent", stroke: '#ffb735', strokeWidth: 0}}/>
        
      </LineChart>
      </ResponsiveContainer>
)}
      else {
          return null;
      }

}

export default TempChart
