import '../styles/App.css';
import CityCurrentWeather from './CityCurrentWeather';
import CityForecast from './CityForecast';
import CitySearchForm from './CitySearchForm';
import '../styles/base.scss'
import { OWCityGetData } from '../functions/OWCityGetData';
import { useSelector } from 'react-redux';
import DaySelector from './DaySelector';
import { Line, LineChart, XAxis, Tooltip, YAxis, CartesianGrid } from 'recharts';

function App() {
  const currentCity = state => state.city;
  const cityName = useSelector(currentCity);
  OWCityGetData(cityName,"weather")
  OWCityGetData(cityName,"forecast")
  return (
    <div className="App">
      <div className = "search-and-result">
        <div className = "search-and-result__search-days-container">
            <CityCurrentWeather/>
            <CitySearchForm/>
        </div>
        <DaySelector/>
      </div>
      <CityForecast/>
      <LineChart
          width={1080}
          height={400}
          data={[{time: "3:00", temp : 4}, {time: "6:00", temp : 8}, {time: "9:00", temp : 11}, {time: "12:00", temp : 13}, {time: "15:00", temp : 15}, {time: "18:00", temp : 12}, {time: "21:00", temp : 6}]}
          margin={{ top: 5, right: 20, left: 10, bottom: 5 }}
      >
          <XAxis dataKey="time" />

          <CartesianGrid stroke="#eee" strokeDasharray="5 5"/>
          <Tooltip />

          {/*<Line type="monotone" dataKey="time" stroke="red" yAxisId={0} label={{ fill: 'red', fontSize: 20, fontWeight: "bold", background: "white" }} />*/}
          <Line type="monotone" dataKey="temp" stroke="red" yAxisId={1} label={{ fill: 'red', fontSize: 20 }} dot={{ stroke: 'red', width: 20}}/>

      </LineChart>
    </div>
  );
}

export default App;
